//1. verwijdert querystrings in URL van javascript en css bestandenfunction _remove_script_version( $src ){$parts = explode( '?ver', $src );return $parts[0];}add_filter( 'script_loader_src', '_remove_script_version', 10, 1 );add_filter( 'style_loader_src', '_remove_script_version', 10, 1 );/*2. functions on behalf of the project types template*/
function rs_Style_ProjectTypes(){    if(is_page('project-types')) //slug als argument      {        wp_register_style('files_style', plugins_url('/rootsandshoots/appcode/webapp/view/css/files.css','_FILE_'), array());        wp_enqueue_style('files_style');         wp_register_style('projecttype_style', plugins_url('/rootsandshoots/appcode/webapp/view/css/projecttypes.css','_FILE_'), array());        wp_enqueue_style('projecttype_style');    } }add_action('wp_enqueue_scripts', 'rs_Style_ProjectTypes');/*w wellicht geladen bij activ plugin*/function rs_Script_ProjectTypes(){    if(is_page('project-types')) //slug als argument    {        wp_register_script('projecttype_script', plugins_url('/rootsandshoots/appcode/webapp/view/jquery/projecttypes.js','_FILE_'), array('jquery'));        wp_enqueue_script('projecttype_script');    }}add_action('wp_enqueue_scripts', 'rs_Script_ProjectTypes');//add_action('admin_enqueue_scripts', 'rs_Script_ProjectTypes');/*3. function on behalf of the project type form template*/
function rs_StyleAndScript_ProjectTypeForm(){        if(is_page('project-type-form')) //slug als argument    {    wp_register_style('files_style', plugins_url('/rootsandshoots/appcode/webapp/view/css/files.css','_FILE_'), array());    wp_enqueue_style('files_style');     wp_register_style('form_style', plugins_url('/rootsandshoots/appcode/webapp/view/css/form.css','_FILE_'), array());    wp_enqueue_style('form_style');     wp_register_script('projecttypeform_script', plugins_url('/rootsandshoots/appcode/webapp/view/jquery/projecttypeform.js','_FILE_'), array('jquery'));    wp_enqueue_script('projecttypeform_script');    }}add_action('wp_enqueue_scripts', 'rs_StyleAndScript_ProjectTypeForm');/*4. function on behalf of all projects shown to administrator*/function rs_StyleAndScript_AllProjects(){    if(is_page('all-projects')) //slug als argument    {        wp_register_style('files_style', plugins_url('/rootsandshoots/appcode/webapp/view/css/files.css','_FILE_'), array());        wp_enqueue_style('files_style');         wp_register_style('project_style', plugins_url('/rootsandshoots/appcode/webapp/view/css/allprojects.css','_FILE_'), array());        wp_enqueue_style('project_style');        wp_register_script('project_script', plugins_url('/rootsandshoots/appcode/webapp/view/jquery/all_projects.js','_FILE_'), array('jquery'));        wp_enqueue_script('project_script');    }}add_action('wp_enqueue_scripts', 'rs_StyleAndScript_AllProjects');/*5. functions on behalf of the project form template*/function rs_StyleAndScript_ProjectForm(){        if(is_page('project-form')) //slug als argument    {    wp_register_style('files_style', plugins_url('/rootsandshoots/appcode/webapp/view/css/files.css','_FILE_'), array());    wp_enqueue_style('files_style');     wp_register_style('projectform_style', plugins_url('/rootsandshoots/appcode/webapp/view/css/projectform.css','_FILE_'), array());    wp_enqueue_style('projectform_style');     wp_register_script('projectform_script', plugins_url('/rootsandshoots/appcode/webapp/view/jquery/projectform.js','_FILE_'), array('jquery'));    wp_enqueue_script('projectform_script');    }}add_action('wp_enqueue_scripts', 'rs_StyleAndScript_ProjectForm');/*6. function on behalf of projects shown to visitors*/function rs_StyleAndScript_Projects(){    if(is_page('projects')) //slug als argument    {        wp_register_style('files_style', plugins_url('/rootsandshoots/appcode/webapp/view/css/files.css','_FILE_'), array());        wp_enqueue_style('files_style');         wp_register_style('project_style', plugins_url('/rootsandshoots/appcode/webapp/view/css/projects.css','_FILE_'), array());        wp_enqueue_style('project_style');        wp_register_script('project_script', plugins_url('/rootsandshoots/appcode/webapp/view/jquery/all_projects.js','_FILE_'), array('jquery'));        wp_enqueue_script('project_script');    }}add_action('wp_enqueue_scripts', 'rs_StyleAndScript_Projects');function recent_posts_function() {   query_posts(array('orderby' => 'date', 'order' => 'DESC' , 'showposts' => 1));   if (have_posts()) :      while (have_posts()) : the_post();         $return_string = '<a href="'.get_permalink().'">'.get_the_title().'</a>';      endwhile;   endif;   wp_reset_query();   return $return_string;}/*function selectAllCountries()        {        $result=FALSE;        if($this->connect())        {            try            {            $preparedStatement=$this->pdo->prepare('call countryselectall()');            $preparedStatement->execute();            if ($result = $preparedStatement->fetchAll())            {                $this->feedback = 'All countries read.';            }            else            {                $this->feedback = 'The table rs_countries is empty.';                $sQLErrorInfo = $preparedStatement->errorInfo();                $this->errorCode = $sQLErrorInfo[0].'/'.$sQLErrorInfo[1]